// Vuetify 自定义样式
@use 'vuetify' with (
  $color-pack: false
);

// 自定义主题变量
$primary: #1976D2;
$secondary: #424242;
$accent: #82B1FF;
$error: #FF5252;
$info: #2196F3;
$success: #4CAF50;
$warning: #FFC107;

// 自定义组件样式
.v-btn {
  text-transform: none !important;
  letter-spacing: normal !important;
}

.v-card {
  border-radius: 12px !important;
}

.v-app-bar {
  backdrop-filter: blur(10px);
}

// 响应式断点自定义
@media (max-width: 600px) {
  .v-container {
    padding: 8px;
  }
}
