module.exports = {
  // JavaScript/TypeScript files
  '*.{js,jsx,ts,tsx}': [
    'eslint --fix',
    'prettier --write'
  ],

  // Vue files
  '*.vue': [
    'eslint --fix',
    'stylelint --fix',
    'prettier --write'
  ],

  // CSS/SCSS files
  '*.{css,scss,less}': [
    'stylelint --fix',
    'prettier --write'
  ],

  // JSON files
  '*.json': [
    'prettier --write'
  ],

  // Markdown files
  '*.md': [
    'prettier --write'
  ],

  // YAML files
  '*.{yml,yaml}': [
    'prettier --write'
  ]
};

